import React, { useRef, useEffect } from "react";

const Sidebar = ({ users = [], currentUserId = null }) => {
  const sideBarRef = useRef(null);

  const openSideBar = () => {
    if (sideBarRef.current) sideBarRef.current.style.transform = "translateX(0)";
  };
  const closeSideBar = () => {
    if (sideBarRef.current) sideBarRef.current.style.transform = "translateX(-100%)";
  };

  // Mở sẵn khi vào (giống ảnh bạn chụp). Nếu muốn đóng mặc định thì xoá useEffect này.
  useEffect(() => {
    openSideBar();
  }, []);

  return (
    <>
      <button
        onClick={openSideBar}
        style={{ position: "fixed", top: 10, left: 10, zIndex: 10000, padding: "6px 10px" }}
      >
        Users
      </button>

      <div
        ref={sideBarRef}
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: 220,
          height: "100vh",
          background: "#1f2329",
          transform: "translateX(-100%)",       // đóng mặc định (đã mở sẵn bằng useEffect ở trên)
          transition: "transform .25s ease",
          zIndex: 9999,
        }}
      >
        <button
          onClick={closeSideBar}
          style={{ width: "100%", padding: 10, background: "#f5f5f5", border: "none" }}
        >
          Close
        </button>

        <div style={{ marginTop: 20 }}>
          {users.map((u) => (
            <p
              key={u.id}
              style={{
                color: "#fff",
                textAlign: "center",
                padding: "8px 0",
                borderBottom: "1px solid rgba(255,255,255,.08)",
                margin: 0,
              }}
            >
              {u.username}
              {u.id === currentUserId ? " - (You)" : ""}
            </p>
          ))}
        </div>
      </div>
    </>
  );
};

export default Sidebar;
